int read(){
    int s = 0, ne = 1; char c = getchar();
    for(;c < '0' || c > '9';c = getchar()) if(c == '-') ne = -1;
    for(;c >= '0' && c <= '9';c = getchar()) s = (s << 1) + (s << 3) + c - '0';
    return s * ne;
}
int qp(int a, int b){
    int r = 1;
    while(b){
        if(b & 1) r = r * a % P;
        a = a * a % P, b >>= 1;
    }
    return r;
}
int inv(int x) {return qp(x, P - 2);}
int n, m;
int f(int x){
    int k = x / 5000000, r = x % 5000000, ans = fac[k];
    while(r--) ans = ans * x % P, x--;
    return ans;
}
